import { useState } from 'react';
import './CostForm.css';

const CostForm = (props) => {
    
    const [inputName, setInputName] = useState('');
    const [inputPrice, setInputPrice] = useState('');
    const [inputDate, setInputDate] = useState('');

    // const [userInput, setUserInput] = useState({
    //     nameIn:'',
    //     priceIn:'',
    //     dateIn:''
    // });

    const nameChangeHandler = (event) => {
        setInputName(event.target.value);
    //    setUserInput({
    //     ...userInput,
    //     name: event.target.value
    //    })
    //    setUserInput((previousState) => {
    //         return {
    //             ...previousState,
    //             nameIn: event.target.value
    //         }
    //    })
    };
    const priceChangeHandler = (event) => {
        setInputPrice(event.target.value);
        // setUserInput({
        //     ...userInput,
        //     priceIn: event.target.value
        // })
    };
    const dateChangeHandler = (event) => {
        setInputDate(event.target.value);
        // setUserInput({
        //     ...userInput,
        //     dateIn: event.target.value
        // })
    };

    const submitHandler = (event) => {
        event.preventDefault();

        const costData = {
            name: inputName,
            price: inputPrice,
            date: new Date(inputDate)
        };

       // console.log(costData);
        props.onSaveCostData(costData);
        setInputName('');
        setInputPrice('');
        setInputDate('');
    };

    return (
    <form onSubmit={submitHandler}>
        <div className="new-cost__controls">
            <div className="new-cost__control">
                <label>Название</label>
                <input type="text" value={inputName} onChange={nameChangeHandler}/>
            </div>
            <div className="new-cost__control">
                <label>Сумма</label>
                <input type="number" min='0.01' step='0.01'
                value={inputPrice} onChange={priceChangeHandler}/>
            </div>
            <div className="new-cost__control">
                <label>Дата</label>
                <input type="date" min='2023-01-01' step='2022-12-31'
                value={inputDate}//двустороннее связывание 
                onChange={dateChangeHandler}/>
            </div>
            <div className="new-cost__actions">
                <button type="submit"> Добавить расход </button>
            </div>

        </div>
    </form>
    );
}

export default CostForm;
