import Events from "./NewComponents/Events/Events";
import NewEvent from "./NewComponents/NewEvent/NewEvent";
import React, { useState } from "react";

//массив данных для отображения
const INITIAL_EVENTS = [
  {
    id : 'c1',
    date: new Date(2021, 8, 4),
    name: 'Baritone',
    mail: '999.999'
  },
  {
    id : 'c2',
    date: new Date(2022, 1, 23),
    name: 'Fridge',
    mail: '5.3'
  },
  {
    id : 'c3',
    date: new Date(2020, 11, 23),
    name: 'iPhone 17',
    mail: '180'
  }
];

function App() {

  const [events, setEvents] = useState(INITIAL_EVENTS);

  // при добавлении нового спред всех предыдущих
  function addEventHandler(event) {
    setEvents((prevEvents) => {
      return [event, ...prevEvents];
    });
    //console.log(cost);
    //console.log('APP');
  }

  return (
    <div>
      <h1>BVT-Force Project</h1>
      <h3>__Система бронирования__</h3>
      {/* объект добавления */}
      <NewEvent onAddEvent={addEventHandler}/> 
      {/* поле где отображаются данные массива */}
      <Events events={events}/>
    </div>
  );
}

export default App;
//основа реакта - компоненты. но в html коде страницы они не
