import Costs from "./components/Costs/Costs";
import NewCost from "./components/NewCost/NewCost";
import React, { useState } from "react";

const INITIAL_COSTS = [
  {
    id : 'c1',
    date: new Date(2021, 8, 4),
    description: 'Baritone',
    price: 999.999
  },
  {
    id : 'c2',
    date: new Date(2022, 1, 23),
    description: 'Fridge',
    price: 5.3
  },
  {
    id : 'c3',
    date: new Date(2020, 11, 23),
    description: 'iPhone 17',
    price: 180
  }
];

function App() {

  const [costs, setCosts] = useState(INITIAL_COSTS);

  function addCostHandler(cost) {
    setCosts((prevCosts) => {
      return [cost, ...prevCosts];
    });
    //console.log(cost);
    //console.log('APP');
  }

  return (
    <div>
      <h1>Get start it again</h1>
      <h2>Text here</h2>
      <NewCost onAddCost={addCostHandler}/>
      <Costs costs={costs}/>
    </div>
  );
}

export default App;
